import re

def clean_svg_numbers(path_str):
 def trim_num(m):
  s = m.group()
  # keep integers like '0', '10', '-5' as is
  if '.' not in s:
   return s
  # remove trailing zeros and dot if useless
  s = s.rstrip('0').rstrip('.')
  # remove leading zero before decimal only if followed by digits
  s = re.sub(r'(?<!\d)0(?=\.\d)', '', s)
  return s
 return re.sub(r'-?\d+\.\d+|-?\d+', trim_num, path_str)

